package com.djw.demo.area.config.dao;import com.mchange.v2.c3p0.ComboPooledDataSource;import org.mybatis.spring.annotation.MapperScan;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import java.beans.PropertyVetoException;@Configuration@MapperScan("com.djw.demo.area")public class DataSourceConfig {    @Value("${jdbc.driver}")    private String driver;    @Value("${jdbc.url}")    private String url;    @Value("${jdbc.userName}")    private String userName;    @Value("${jdbc.password}")    private String password;    @Bean(name = "dataSource")    public ComboPooledDataSource createDataSource() throws PropertyVetoException {        ComboPooledDataSource dataSource = new ComboPooledDataSource();        dataSource.setDriverClass(driver);        dataSource.setJdbcUrl(url);        dataSource.setUser(userName);        dataSource.setPassword(password);        dataSource.setAutoCommitOnClose(false);        return dataSource;    }}