package com.djw.demo.area.controller;import com.djw.demo.area.entity.Area;import com.djw.demo.area.service.AreaService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;@RestController@RequestMapping("/admin")public class AreaController {    @Autowired    private AreaService areaService;    @RequestMapping(value = "/listArea", method = RequestMethod.GET)    public Map<String, Object> listArea(){        Map<String, Object> listMap = new HashMap<>();        List<Area> list = new ArrayList<Area>();        list = areaService.queryArea();        listMap.put("areaList",list);        return listMap;    }    @RequestMapping(value = "/getAreaById", method = RequestMethod.GET)    public Map<String, Object> getAreaById(@RequestBody int areaId){        Map<String, Object> listMap = new HashMap<>();        Area area = areaService.queryAreaById(areaId);        listMap.put("area",area);        return  listMap;    }    @RequestMapping(value = "/insertArea", method = RequestMethod.POST)    public Map<String, Object> insertArea(@RequestBody Area area){        Map<String, Object> listMap = new HashMap<>();        boolean isSuccess = areaService.insertArea(area);        listMap.put("success",isSuccess);        return listMap;    }    @RequestMapping(value = "/updateArea", method = RequestMethod.POST)    public Map<String, Object> updateArea(@RequestBody Area area){        Map<String, Object> listMap = new HashMap<>();        boolean isSuccess = areaService.updateArea(area);        listMap.put("success",isSuccess);        return listMap;    }    @RequestMapping(value = "/deleteArea", method = RequestMethod.POST)    public Map<String, Object> deleteArea(@RequestBody Area area){        Map<String, Object> listMap = new HashMap<>();        boolean isSuccess = areaService.deleteArea(area.getAreaId());        listMap.put("success",isSuccess);        return listMap;    }}