package com.djw.demo.area.controller;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import javax.servlet.http.HttpServletRequest;import java.util.HashMap;import java.util.Map;@ControllerAdvicepublic class GlobalExceptionHandler {    @ExceptionHandler(value = Exception.class)    private Map<String, Object> exceptionHandler(HttpServletRequest request, Exception e){        Map<String, Object> modeMap = new HashMap<>();        modeMap.put("success",false);        modeMap.put("errorMsg", e.getMessage());        return  modeMap;    }}