package com.djw.demo.area.dao;import com.djw.demo.area.entity.Area;import org.junit.Ignore;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.Date;import java.util.List;import static org.junit.Assert.*;@RunWith(SpringRunner.class)@SpringBootTestpublic class AreaDaoTest {    @Autowired    private AreaDao areaDao;    @Test    @Ignore    public void queryArea(){        List<Area> areaList = areaDao.queryArea();        assertEquals(2, areaList.size());    }    @Test    @Ignore    public void queryAreaById(){        Area area = areaDao.queryAreaById(1);        assertEquals("东城",area.getAreaName());    }    @Test    @Ignore    public void insertArea(){        Area area = new Area();        area.setAreaName("西城");        area.setPriority(3);        area.setCreateTime(new Date());        area.setLastEditTime(new Date());        int count = areaDao.insertArea(area);        assertEquals(1,count);    }    @Test    @Ignore    public void updateArea(){        Area area = new Area();        area.setAreaId(2);        area.setAreaName("男成");        area.setPriority(3);        area.setCreateTime(new Date());        area.setLastEditTime(new Date());        int count = areaDao.updateArea(area);        assertEquals(1,count);    }    @Test    @Ignore    public void deleteArea(){        int count = areaDao.deleteArea(3);        assertEquals(1,count);    }}